mapper.insert:
        /* insertNews - 뉴스등록 */

		INSERT INTO tbl_news ( comp_cd
		                     , cl_cd
		                     , news_title
		                     , news_content
		                     , news_url
		                     , pub_dt
		                     , reg_dt
		                     , upd_dt
		                     )
		VALUES ( #{compCd}
		       , #{clCd}
		       , #{newsTitle}
		       , #{newsContent}
		       , #{newsUrl}
		       , #{pubDt}
		       , NOW()
		       , NOW()
		       );

mapper.update:
/* updateNews - 뉴스수정 */

UPDATE tbl_news


        AND news_title = #{newsTitle}  /* 뉴스 제목 */


        AND pub_dt = #{pubDt}  /* 공개일시 */


        AND news_content = #{newsContent}  /* 뉴스 내용 */


WHERE news_seq = #{newsSeq}
